import React, { useState, useEffect } from 'react'
import { isoFlipped, ISO } from '../info'

const Display = ({data, unit, isDayTime}) => {
    const [country, setCountry] = useState('United States')

    const divStyle = {
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center',  
        height: '100vh', 
        width: '50%', 
        minWidth: 500,
        minHeight: 500,
        backgroundColor: isDayTime ? '#ffffff' : '#363636'
    }

    const countries = Object.keys(isoFlipped)

    useEffect(() => {
        if(data !== null && 'sys' in data) {
            setCountry(ISO[data.sys.country])
        }
    }, [data])

    const handleCountryChange = (e) => {
        // Add callback for parent to change country to zipcode lookup
        setCountry(e.target.value)
    }
    
    console.log(data)

    let currentWeather 

    if(data !== null) {
        currentWeather = 
            <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center'}}>
                <h1 style={{letterSpacing: 2, fontSize: '2rem'}} >{data.name}, {country}</h1>
                <p style={{letterSpacing: 1, fontSize: '1.5rem', marginTop: 10}} >{Math.floor(data.main.temp)}<span>&#176;</span> {unit === 'imperial' ? 'Farenheit' : 'Celcius'}</p>
                <p style={{letterSpacing: 1, fontSize: '1.5rem', marginTop: 10}}>{data.weather[0].description}</p>
                <img src='/static/001-rain-3.svg' />
            </div>
    }

    return (
        <div style={divStyle}>
            <select onChange={(e) => handleCountryChange(e)}  value={country}>
                {countries.map(item => <option key={item} >{item}</option>)}
            </select>
            {currentWeather}
        </div>

    )
}

export default Display
